        FAQ4QH2.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ4QH2.setText("<html><b>What should I do if my baggage is damaged in transit?\n");
        FAQ4Panel.add(FAQ4QH2, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 305, -1, 30));

        FAQ4QC2.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC2.setText("<html>\nIf your baggage has been damaged in transit, you need to file a claim before you leave the airport by approaching the nearest representative or the baggage services desk. This can usually be found in the customs hall, near the baggage claim area.");
        FAQ4QC2.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ4Panel.add(FAQ4QC2, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 355, 1060, 50));

        FAQ4QC3.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC3.setText("<html>\nIf your baggage has been held by customs, we’ll do our best to coordinate its release. In some cases, you may be asked to provide an authority letter authorising the opening of your baggage.\n<br><br>\nIf we’re unable to coordinate the release of baggage, you will be contacted by our team to receive a full update.");
        FAQ4QC3.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ4Panel.add(FAQ4QC3, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 500, 1060, 90));

