        FAQ6QH1.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ6QH1.setText("<html><b>What personal electronic devices can I use on board?   ");
        FAQ6Panel.add(FAQ6QH1, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 30, -1, 30));

        FAQ6QC1.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ6QC1.setText("Yes, you can pay for a data plan using your debit or credit card on board.\n\n");
