        FAQ4QH8.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ4QH8.setText("<html><b>Can I use e-cigarettes on board an Emirates flight? ");
        FAQ8Panel.add(FAQ4QH8, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 30, -1, 30));

        FAQ4QC8.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC8.setText("<html>\nAll Emirates flights are non-smoking – this also includes the use of e-cigarettes. No e-cigarettes can be used on board Emirates flights.\n");
        FAQ4QC8.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ8Panel.add(FAQ4QC8, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 80, 1060, 30));

        FAQ4QB9.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);
