        FAQ8Panel.add(FAQ4QB9, new org.netbeans.lib.awtextra.AbsoluteConstraints(30, 160, 40, 30));

        FAQ4QH9.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ4QH9.setText("<html><b>Is smoking allowed on any Emirates flights? ");
        FAQ8Panel.add(FAQ4QH9, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 160, -1, 30));

        FAQ4QC9.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC9.setText("<html>\nAll Emirates flights are non-smoking. All Emirates codeshare flights are also non-smoking. See codeshare agreements for more details.\n<br><br>\nThe non-smoking policy also includes the use of e-cigarettes. No e-cigarettes can be used on board Emirates flights.");
        FAQ4QC9.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ8Panel.add(FAQ4QC9, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 210, 1060, 80));

