        FAQ4QB10.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);
        FAQ4QB10.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Images/Bullet.png"))); // NOI18N
        FAQ10Panel.add(FAQ4QB10, new org.netbeans.lib.awtextra.AbsoluteConstraints(30, 30, 40, 30));

        FAQ4QH10.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ4QH10.setText("<html><b>Can I get a refund if I cancel my online booking? ");
        FAQ10Panel.add(FAQ4QH10, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 30, -1, 30));

        FAQ4QC10.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC10.setText("<html>\nGenerally, if allowed by the fare conditions of the booking, you may cancel your online booking and receive a refund depending on the cancellation/refund status of your ticket. We’ll refund the credit or debit card used to purchase the ticket. If you paid using another form of payment, you’ll be advised on the refund form of your options for receiving your refund.\n<br><br>\nPlease note there may be a cancellation penalty, which will be deducted from the refund. Some tickets are non-refundable. Please check your fare conditions carefully before requesting a refund.");
        FAQ4QC10.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ10Panel.add(FAQ4QC10, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 80, 1060, 140));

        FAQ4QB11.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);
        FAQ4QB11.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Images/Bullet.png"))); // NOI18N
        FAQ10Panel.add(FAQ4QB11, new org.netbeans.lib.awtextra.AbsoluteConstraints(30, 270, 40, 30));

        FAQ4QH11.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 24)); // NOI18N
        FAQ4QH11.setText("<html><b>What are fare conditions? ");
        FAQ10Panel.add(FAQ4QH11, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 270, -1, 30));

        FAQ4QC11.setFont(new java.awt.Font("Microsoft JhengHei UI", 0, 16)); // NOI18N
        FAQ4QC11.setText("<html>All air tickets have fare conditions attached to them which determine the price of that ticket. The most common conditions to be aware of are those governing whether a ticket is refundable and whether additional charges will be imposed in the event that your travel plans change.\n<br><br>\nDifferent fares will have different conditions, and a ticket that allows you to change the dates of your travel without charge – or to receive a full refund if you cancel your travel plans – will be more expensive, but useful if you think your travel plans might change.");
        FAQ4QC11.setVerticalAlignment(javax.swing.SwingConstants.TOP);
        FAQ10Panel.add(FAQ4QC11, new org.netbeans.lib.awtextra.AbsoluteConstraints(70, 320, 1060, 150));

